#!/bin/shactor -d0 -n stealth_sensor2 -h 10.5.1.12 -p 9850 << CONFIG10.5.1.12 9800 midi   note_on   midi_channel 4   low   0x02   hi    0x02   low_velocity   0x00   hi_velocity    0x7f   endset activity 1set switcher 110.5.1.14 9901 :Axdx\rif %frame -gt 54000	{	shell makerandom 200 500 > currentrandom.txt	shell hpcp -d0 -l -h 10.5.1.12 -p 9850 -f currentrandom.txt	set frame %random	}set frame %frame+%random10.5.1.17 9901 : %frame se 30 sp mf\rif %xlimit -ne 1	{	# jog left	10.5.1.17 9900 :ax jg-800	}	CONFIG