#!/bin/bash# maiden laser plays on any noteactor -d1 -n maiden -h 10.5.1.9 -p 9850 << CONFIG10.5.1.9 9800 midi   note_on   midi_channel 0    low   0x01    hi    0x7f    low_velocity   0x01   hi_velocity    0x7f    endif %frame -gt 35750	{	shell makerandom 300 700 > currentrandom.txt	shell hpcp -d0 -l -h 10.5.1.9 -p 9850 -f currentrandom.txt	set frame %random	}set frame %frame+%randomif %frame -lt 35750	{	10.5.1.9 9906 : %frame se pl\r	}	CONFIG