#!/bin/shactor -d0 -n picdd_reset04_home1 -h 10.5.1.3 -p 9850 << CONFIGlocalhost 0 immediate   init_delay 0   loop_delay 0   iterations 1   endshell picdd_reset04_stat.sh &shell msleep 15010.5.1.3 9700 :stat 04shell msleep 100if %location04 -le 16   {	10.5.1.3 9700 :vel 04 200000	10.5.1.3 9700 :vmode 04 rev	}shell picdd_reset04_center.sh &shell picdd_reset04_ping.sh &shell msleep 500#10.5.1.3 9700 :home1 0410.5.1.3 9700 :vel 04 20000010.5.1.3 9700 :vmode 04 forCONFIG