#!/bin/shactor -d0 -n picdd_sensor2 -h 10.5.1.3 -p 9850 << CONFIGlocalhost 0 immediate   init_delay 0   loop_delay 0   iterations 0   endshell msleep 10010.5.1.3 9700 : ping 01shell msleep 100set diff2 %rail01_midi-%sensor2_midiif %rail01_midi -gt 48	{	set invert01_midi %rail01_midi-48	set invert01_midi 48-%invert01_midi	}if %rail01_midi -lt 48	{	set invert01_midi 48-%rail01_midi	set invert01_midi 48+%invert01_midi	}	set light_x %invert01_midi*100	#set light_x %rail01_midi*100set light_x %light_x/382set light_x %light_x-3#shell echo lights are %light_x %light_y set current2_midi %sensor2_midi   if %current2_midi -ge 80   {   if %go_middle_x -eq 0      {      10.5.1.3 9700 :stop 131      shell msleep 700      10.5.1.3 9700 :vel 131 50000      10.5.1.3 9700 :vel 01 100000      10.5.1.3 9700 :pos 131 -4000      10.5.1.3 9700 :pos 01 0      10.5.1.3 9700 :gtraj 131      10.5.1.3 9700 :gtraj 01      10.5.1.3 9900 :blank\r      set go_middle_x 1      }   }if %current2_midi -lt 80   {   if %diff2 -ge 15      {      10.5.1.3 9900 :+ %light_x %light_y \r      10.5.1.3 9700 :vel 131 200000      10.5.1.3 9700 :vel 01 500000      10.5.1.3 9700 :vmode 131 for      10.5.1.3 9700 :vmode 01 for      }   }   if %current2_midi -lt 80   {   if %diff2 -lt 15      {         if %diff2 -ge 7         {			10.5.1.3 9900 :+ %light_x %light_y \r         10.5.1.3 9700 :vel 131 100000         10.5.1.3 9700 :vel 01 250000         10.5.1.3 9700 :vmode 131 for         10.5.1.3 9700 :vmode 01 for         }      }   }   if %current2_midi -lt 80   {   if %diff2 -lt 7      {         if %diff2 -ge 3         {         10.5.1.3 9900 :+ %light_x %light_y \r         10.5.1.3 9700 :vel 131 50000         10.5.1.3 9700 :vel 01 125000         10.5.1.3 9700 :vmode 131 for         10.5.1.3 9700 :vmode 01 for         }      }   }   if %current2_midi -lt 80	{	if %diff2 -lt 3	   {	      if %diff2 -gt -3	      {         10.5.1.3 9900 :+ %light_x %light_y \r	      10.5.1.3 9700 :vel 131 0 	      10.5.1.3 9700 :vmode 131 rev      	}   	}	}   if %current2_midi -lt 80   {   if %diff2 -le -15      {      10.5.1.3 9900 :+ %light_x %light_y \r      10.5.1.3 9700 :vel 131 200000      10.5.1.3 9700 :vel 01 500000      10.5.1.3 9700 :vmode 131 rev      10.5.1.3 9700 :vmode 01 rev      }   }   if %current2_midi -lt 80   {   if %diff2 -gt -15      {         if %diff2 -le -7         {         10.5.1.3 9900 :+ %light_x %light_y \r         10.5.1.3 9700 :vel 131 100000         10.5.1.3 9700 :vel 01 250000         10.5.1.3 9700 :vmode 131 rev         10.5.1.3 9700 :vmode 01 rev         }      }   }if %current2_midi -lt 80   {   if %diff2 -gt -7      {         if %diff2 -le -3         {         10.5.1.3 9900 :+ %light_x %light_y \r         10.5.1.3 9700 :vel 131 50000         10.5.1.3 9700 :vel 01 125000         10.5.1.3 9700 :vmode 131 rev         10.5.1.3 9700 :vmode 01 rev         }      }    }      CONFIG