#!/bin/shactor -d0 -n picdd_sensor3 -h 10.5.1.3 -p 9850 << CONFIGlocalhost 0 immediate   init_delay 0   loop_delay 0   iterations 0   endshell msleep 10010.5.1.3 9700 : ping 04shell msleep 100set diff3 %rail04_midi-%sensor3_midi   if %rail04_midi -gt 48	{	set invert04_midi %rail04_midi-48	set invert04_midi 48-%invert04_midi	}if %rail04_midi -lt 48	{	set invert04_midi 48-%rail04_midi	set invert04_midi 48+%invert04_midi	}	set light_y %invert04_midi*100	#set light_y %rail04_midi*100set light_y %light_y/382set light_y %light_y-3set current3_midi %sensor3_midiif %current3_midi -ge 80   {   if %go_middle_y -eq 0      {      10.5.1.3 9700 :stop 132      shell msleep 700      10.5.1.3 9700 :vel 132 50000      10.5.1.3 9700 :vel 04 100000      10.5.1.3 9700 :pos 132 4000      10.5.1.3 9700 :pos 04 0      10.5.1.3 9700 :gtraj 132      10.5.1.3 9700 :gtraj 04      10.5.1.3 9900 :blank\r      set go_middle_y 1      }   }if %current3_midi -lt 80   {   if %diff3 -ge 15      {      10.5.1.3 9900 :+ %light_x %light_y \r      10.5.1.3 9700 :vel 132 200000      10.5.1.3 9700 :vel 04 500000      10.5.1.3 9700 :vmode 132 for      10.5.1.3 9700 :vmode 04 for     }   }   if %current3_midi -lt 80   {   if %diff3 -lt 15      {         if %diff3 -ge 7         {         10.5.1.3 9900 :+ %light_x %light_y \r         10.5.1.3 9700 :vel 132 100000         10.5.1.3 9700 :vel 04 250000         10.5.1.3 9700 :vmode 132 for         10.5.1.3 9700 :vmode 04 for         }      }   }   if %current3_midi -lt 80   {   if %diff3 -lt 7      {         if %diff3 -ge 3         {         10.5.1.3 9900 :+ %light_x %light_y \r         10.5.1.3 9700 :vel 132 50000         10.5.1.3 9700 :vel 04 125000         10.5.1.3 9700 :vmode 132 for         10.5.1.3 9700 :vmode 04 for         }      }   }   if %current3_midi -lt 80	{	if %diff3 -lt 3	   {	      if %diff3 -gt -3	      {         10.5.1.3 9900 :+ %light_x %light_y \r	      10.5.1.3 9700 :vel 132 0 	      10.5.1.3 9700 :vmode 132 rev	      }	   }	}   if %current3_midi -lt 80   {   if %diff3 -le -15      {      10.5.1.3 9900 :+ %light_x %light_y \r      10.5.1.3 9700 :vel 132 200000      10.5.1.3 9700 :vel 04 500000      10.5.1.3 9700 :vmode 132 rev      10.5.1.3 9700 :vmode 04 rev      }   }   if %current3_midi -lt 80   {   if %diff3 -gt -15      {         if %diff3 -le -7         {         10.5.1.3 9900 :+ %light_x %light_y \r         10.5.1.3 9700 :vel 132 100000         10.5.1.3 9700 :vel 04 250000         10.5.1.3 9700 :vmode 132 rev         10.5.1.3 9700 :vmode 04 rev        }      }   }if %current3_midi -lt 80   {   if %diff3 -gt -7      {         if %diff3 -le -3         {         10.5.1.3 9900 :+ %light_x %light_y \r         10.5.1.3 9700 :vel 132 50000         10.5.1.3 9700 :vel 04 125000         10.5.1.3 9700 :vmode 132 rev         10.5.1.3 9700 :vmode 04 rev        }      }    }      CONFIG