#!/bin/sh# recalibrate every 20 minactor -d0 -n rails_calibrate -h 10.5.1.10 -p 9850 << CONFIGlocalhost 0 immediate   init_delay 0   loop_delay 1200   iterations 0   endif %startup -ne 1	{	10.5.1.10 9901 :F0\rD5@0\rG\r	shell cat /var/run/actor.picdd_rail01_midi.pid | xargs kill	shell cat /var/run/actor.picdd_rail_sensor2_midi.pid | xargs kill	shell cat /var/run/actor.picdd_sensor2.pid | xargs kill	shell cat /var/run/actor.picdd_rail04_midi.pid | xargs kill	shell cat /var/run/actor.picdd_rail_sensor3_midi.pid | xargs kill 	shell cat /var/run/actor.picdd_sensor3.pid | xargs kill	}set startup 0shell /actors/picdd_reset_all_home1.sh &shell sleep 2510.5.1.10 9901 :F0\rD5@100\rG\rshell /actors/picdd_sensor2+3_start.sh &CONFIG