#!/bin/bashactor -d1 -n scribe_pageturn5 -h 10.5.1.14 -p 9850 << CONFIGlocalhost 0 immediate   init_delay 0   loop_delay 0   iterations 20   endif %counter -eq %loop	{	# book opens	10.5.1.14 9900 :FEL10	shell sleep 5	}	# rotate to page10.5.1.14 9900 :FEK40shell msleep 500#extension out10.5.1.14 9900 :FEK20shell msleep 2500# vacuum on10.5.1.14 9900 :FEK08shell msleep 500# probe down10.5.1.14 9900 :FEK04shell msleep 1000# probe up10.5.1.14 9900 :FEL04shell msleep 200# turn page10.5.1.14 9900 :FEL40shell msleep 1200# vacuum off10.5.1.14 9900 :FEL08# swing out10.5.1.14 9900 :FEK80#10.5.1.14 9900 :FEL04shell sleep 2#extension in10.5.1.14 9900 :FEL20shell sleep 5# swing in10.5.1.14 9900 :FEL80shell sleep 2set counter %counter+1set loop %counter/5set loop %loop*5# modulus for match on 5'sshell echo  counter %counter  loop %loopif %counter -eq %loop	{	# book closes	10.5.1.14 9900 :FEK10	shell sleep 5	}if %counter -eq 20	{	# book opens	10.5.1.14 9900 :FEL10	}CONFIG