#!/bin/bashactor -d1 -n scribe_readwrite -h 10.5.1.14 -p 9850 << CONFIG10.5.1.14 9800 midi   note_on   midi_channel 0    low   0x50   hi    0x50   low_velocity   0x01   hi_velocity    0x70    endif %page -eq 1	{	# open book		10.5.1.14 9900 :FEL10	shell /actors/scribe_ocr_process.sh &	}	if %page -gt 1	{	if %page -lt 6		{		shell /actors/scribe_turnpage.sh &		shell sleep 60				}	}	if %page -eq 6	{	shell /actors/scribe_turnpage.sh &	shell sleep 60	10.5.1.14 9900 :FEK10	set page 0	}set page %page+1if %plot -eq 5	{	# pen to top	10.5.1.14 9902 :^^^^^^^^^^^^^^^^^^^^^^^^^^\r	shell sleep 10	shell serialdd -port 9902 -sdev cud2 -ic 50000 &	set plot 1	}	set plot %plot+1CONFIG