#!/bin/bashactor -d1 -n scribe_turnpage -h 10.5.1.14 -p 9850 << CONFIGlocalhost 0 immediate   init_delay 0   loop_delay 0   iterations 1   end	# rotate to page10.5.1.14 9900 :FEK40shell msleep 500#extension out10.5.1.14 9900 :FEK20shell /actors/scribe_ocr_process.sh &shell msleep 2500# vacuum on10.5.1.14 9900 :FEK08shell msleep 500# probe down10.5.1.14 9900 :FEK04shell msleep 1000# probe up10.5.1.14 9900 :FEL04shell msleep 200# turn page10.5.1.14 9900 :FEL40shell msleep 1200# vacuum off10.5.1.14 9900 :FEL08# swing out10.5.1.14 9900 :FEK80#10.5.1.14 9900 :FEL04shell sleep 2#extension in10.5.1.14 9900 :FEL20shell sleep 5# swing in10.5.1.14 9900 :FEL80CONFIG