#!/bin/bashactor -d1 -n usrping.sh -h 10.5.1.16 -p 9850 << CONFIGlocalhost 0 immediate   init_delay 0   loop_delay    iterations 0   end# set the digital output low - arm#10.5.1.16 9905 :FAK000110.5.1.16 9905 :FAk000101shell msleep 100# read the analog input10.5.1.16 9905 :FDL0001shell msleep 500# set the digital output high - ping#10.5.1.16 9905 :FAL000110.5.1.16 9905 :FAk000101shell msleep 100# read the analog input10.5.1.16 9905 :FDL0001shell msleep 500CONFIG